const mongoose = require('mongoose');
const Joi = require('joi');

// Category Schema for Mongoose with validation constraints
const categorySchema = mongoose.Schema({
    name: {
        type: String,
        required: true,
        unique: true,
        minlength: 3,
        maxlength: 50,
    },
}, { timestamps: true });

// Joi Validation Schema for Category Data
const validateCategory = (data) => {
    const schema = Joi.object({
        name: Joi.string().min(3).max(50).required(), // Name between 3-50 characters
    });

    return schema.validate(data);
};

// Export both Mongoose Model and Joi Validation function
const Category = mongoose.model('Category', categorySchema);

module.exports = {
    Category,
    validateCategory,
};
